script:
frappe.ui.form.on('Customer', {
	refresh(frm) {
		// your code here
	},
	validate: async (frm) => {
	    if (!frm.is_new())
	        return;

		let exist;
		frappe.call({
			async: false,
			method: "frappe.client.get",
			args: {
				"doctype": "Customer",
				"filters": {"contact_no": frm.doc.contact_no}
			},
			callback: function (r) {
				if(r.message){
				    console.log(r.message);
				    exist = true;
				}
			}
		});

		if (exist) {
		    let prompt = new Promise((resolve, reject) => {
                frappe.confirm('Duplicate contact no. Do you want to continue?',
                    () => resolve(),
                    () => reject()
                );
            });
            await prompt.then(
                () => frappe.show_alert("Customer Created"),
                () => {
                    frappe.validated = false;
                }
            );
		}
	},
});
