script:
frappe.ui.form.on('Customer', {
	refresh(frm) {
		// your code here
	},
	validate: async (frm) => {
	    if (!frm.is_new())
	        return;

		let exist;
		frappe.call({
			async: false,
			method: "frappe.client.get",
			args: {
				"doctype": "Customer",
				"filters": {"contact_no": frm.doc.contact_no}
			},
			callback: function (r) {
				if(r.message){
				    console.log(r.message);
				    exist = true;
				}
			}
		});

		if (exist) {
		    let prompt = new Promise((resolve, reject) => {
                frappe.confirm('Duplicate contact no. Do you want to continue?',
                    () => resolve(),
                    () => reject()
                );
            });
            await prompt.then(
                () => frappe.show_alert("Customer Created"),
                () => {
                    frappe.validated = false;
                }
            );
		}
	},
});

frappe.ui.form.on('Quality Review', {
	refresh(frm) {
		frm.set_query('goal', () => {
		    return {
				filters: {
					'service': frm.doc.service
				}
			};
		});
	}
});

frappe.ui.form.on('Task', {
	refresh(frm) {
        if(frm.doc.matter) {
            frappe.db.get_value('Matter', frm.doc.matter, 'matter_type', (r) => {
                frm.set_query('type', () => {
                    return {
                        filters: {
                            'matter_type': r.matter_type
                        }
                    };
                });
            });   
        }
	},
	type: function(frm) {
        frappe.db.get_value('Task Type', frm.doc.type, 'description', (r) => {
            if (r && r.description) {
                frm.set_value('subject', r.description);
            }
        });
	}
});
